import React, { useState } from "react";
import {
  AppBar,
  Toolbar,
  IconButton,
  Typography,
  Button,
  Box,
  Menu,
  MenuItem,
  useMediaQuery,
} from "@mui/material";
import { Link } from "react-router-dom";
import MenuIcon from "@mui/icons-material/Menu"; // MUI Menü İkonu

const Header = () => {
  const [anchorEl, setAnchorEl] = useState(null);
  const isMobile = useMediaQuery("(max-width: 765px)");

  const handleMenuOpen = (event) => {
    setAnchorEl(event.currentTarget);
  };

  const handleMenuClose = () => {
    setAnchorEl(null);
  };

  return (
    <AppBar
      position="static"
      sx={{
        backgroundColor: "#1e3a8a", // Navy Blue
        borderBottom: "2px solid #4f46e5", // Highlight
      }}
    >
      <Toolbar
        sx={{
          justifyContent: isMobile ? "space-between" : "space-around",
          alignItems: "center",
        }}
      >
        {/* Sol Bölüm */}
        {!isMobile && (
          <Button
            component={Link}
            to="/login"
            sx={{
              color: "#ffffff",
              fontFamily: "Poppins, sans-serif",
              fontWeight: "600",
              textTransform: "uppercase",
              "&:hover": { backgroundColor: "#4f46e5" }, // Highlight Hover
            }}
          >
            Login
          </Button>
        )}

        {/* Ortada Başlık */}
        <Typography
          variant="h5"
          component="div"
          sx={{
            color: "#ffffff",
            fontFamily: "Poppins, sans-serif",
            fontWeight: "700",
            textTransform: "uppercase",
            letterSpacing: "0.1em",
          }}
        >
          Travel Planner AI
        </Typography>

        {/* Sağ Bölüm */}
        {!isMobile ? (
          <Button
            component={Link}
            to="/signup"
            sx={{
              color: "#ffffff",
              fontFamily: "Poppins, sans-serif",
              fontWeight: "600",
              textTransform: "uppercase",
              "&:hover": { backgroundColor: "#4f46e5" },
            }}
          >
            Sign Up
          </Button>
        ) : (
          <IconButton
            color="inherit"
            onClick={handleMenuOpen}
            aria-label="menu"
          >
            <MenuIcon />
          </IconButton>
        )}

        {/* Mobil Menü */}
        <Menu
          anchorEl={anchorEl}
          open={Boolean(anchorEl)}
          onClose={handleMenuClose}
          PaperProps={{
            sx: {
              backgroundColor: "#1e3a8a", // Menü Arkaplan
              color: "#ffffff", // Yazı Rengi
              "& .MuiMenuItem-root": {
                padding: "12px 24px",
                fontFamily: "Poppins, sans-serif",
                "&:hover": {
                  backgroundColor: "#4f46e5", // Highlight Hover
                },
              },
            },
          }}
        >
          <MenuItem onClick={handleMenuClose} component={Link} to="/login">
            Login
          </MenuItem>
          <MenuItem onClick={handleMenuClose} component={Link} to="/signup">
            Sign Up
          </MenuItem>
        </Menu>
      </Toolbar>
    </AppBar>
  );
};

export default Header;
