import React from "react";
import { Typography, Box, TextField, Button } from "@mui/material";
import { styled } from "@mui/system";

const GradientBackground = styled(Box)(({ theme }) => ({
  background: "linear-gradient(120deg, #12293d 0%, #12465f 100%)", // color1 ve color2
  height: "86vh",
  display: "flex",
  flexDirection: "column",
  justifyContent: "center",
  alignItems: "center",
  textAlign: "center",
  padding: theme.spacing(4),
}));

const SearchBar = styled(TextField)(({ theme }) => ({
  width: "100%",
  maxWidth: "600px",
  backgroundColor: "#ffffff", // Beyaz arka plan
  borderRadius: "8px",
  boxShadow: "0 4px 10px rgba(0, 0, 0, 0.1)",
  "& .MuiOutlinedInput-root": {
    borderRadius: "8px",
    "& fieldset": {
      borderColor: "#146f87", // color3
    },
    "&:hover fieldset": {
      borderColor: "#1aa3b5", // color4
    },
    "&.Mui-focused fieldset": {
      borderColor: "#20dbd8", // color5
    },
  },
  "& input": {
    fontSize: "1rem",
    fontFamily: "Poppins, sans-serif",
    color: "#12465f", // color2
  },
}));

const CustomSearchButton = styled(Button)(({ theme }) => ({
  width: "180px",
  height: "50px",
  borderRadius: "5px",
  border: "none",
  display: "flex",
  alignItems: "center",
  justifyContent: "center",
  background: "#040f16", // Darker background color
  color: "#f5f5f5", // White text color
  fontSize: "16px",
  fontFamily: "Verdana, Geneva, Tahoma, sans-serif",
  fontWeight: "600",
  transition: "all 0.5s ease-in-out",
  position: "relative",
  "&:hover": {
    boxShadow: "0 0 20px 0px #2e2e2e3a",
  },
  "& .icon": {
    position: "absolute",
    height: "40px",
    width: "70px", // Icon için genişlik
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
    transition: "all 0.5s",
  },
  "& .text": {
    transform: "translateX(55px)",
  },
  "&:hover .icon": {
    width: "175px",
  },
  "&:hover .text": {
    transition: "all 0.5s",
    opacity: 0,
  },
  "&:focus": {
    outline: "none",
  },
  "&:active .icon": {
    transform: "scale(0.85)",
  },
}));

const HomePage = () => {
  return (
    <GradientBackground>
      <Typography
        variant="h3"
        component="h1"
        sx={{
          fontFamily: "Poppins, sans-serif",
          fontWeight: "700",
          letterSpacing: "0.05em",
          marginBottom: 3,
          color: "#ffffff", // Beyaz metin
        }}
      >
        Hayalindeki Rotayı Keşfet!
      </Typography>
      <SearchBar
        placeholder="Nereye gitmek istersiniz?"
        variant="outlined"
        sx={{
          marginBottom: 2,
        }}
      />
      <CustomSearchButton>
        <span className="icon">
          <svg
            xmlns="http://www.w3.org/2000/svg"
            width={20}
            height={24}
            fill="currentColor"
            className="bi bi-airplane-fill"
            viewBox="0 0 16 16"
          >
            <path d="M6.428 1.151C6.708.591 7.213 0 8 0s1.292.592 1.572 1.151C9.861 1.73 10 2.431 10 3v3.691l5.17 2.585a1.5 1.5 0 0 1 .83 1.342V12a.5.5 0 0 1-.582.493l-5.507-.918-.375 2.253 1.318 1.318A.5.5 0 0 1 10.5 16h-5a.5.5 0 0 1-.354-.854l1.319-1.318-.376-2.253-5.507.918A.5.5 0 0 1 0 12v-1.382a1.5 1.5 0 0 1 .83-1.342L6 6.691V3c0-.568.14-1.271.428-1.849Z" />
          </svg>
        </span>
        <span className="text">Arama Yap</span>
      </CustomSearchButton>
    </GradientBackground>
  );
};

export default HomePage;
