import React, { useState } from "react";
import {
  Container,
  Typography,
  Button,
  Box,
  TextField,
  Paper,
} from "@mui/material";
import { styled } from "@mui/system";
import { motion } from "framer-motion"; // Framer Motion'ı ekliyoruz

const GradientBackground = styled(Box)(({ theme }) => ({
  background: "linear-gradient(120deg, #12293d 0%, #12465f 100%)", // color1 ve color2
  height: "86vh",
  display: "flex",
  flexDirection: "column",
  justifyContent: "center",
  alignItems: "center",
  textAlign: "center",
  padding: theme.spacing(4),
}));

const SearchBar = styled(TextField)(({ theme }) => ({
  width: "100%",
  maxWidth: "600px",
  backgroundColor: "#ffffff", // Beyaz arka plan
  borderRadius: "8px",
  boxShadow: "0 4px 10px rgba(0, 0, 0, 0.1)",
  "& .MuiOutlinedInput-root": {
    borderRadius: "8px",
    "& fieldset": {
      borderColor: "#146f87", // color3
    },
    "&:hover fieldset": {
      borderColor: "#1aa3b5", // color4
    },
    "&.Mui-focused fieldset": {
      borderColor: "#20dbd8", // color5
    },
  },
  "& input": {
    fontSize: "1rem",
    fontFamily: "Poppins, sans-serif",
    color: "#12465f", // color2
  },
}));

const SearchButton = styled(Button)(({ theme }) => ({
  marginTop: theme.spacing(2),
  padding: theme.spacing(1, 4),
  backgroundColor: "#1aa3b5", // color4
  color: "#ffffff", // Beyaz metin
  fontSize: "1rem",
  fontFamily: "Poppins, sans-serif",
  fontWeight: "bold",
  borderRadius: "24px",
  textTransform: "uppercase",
  transition: "all 0.3s ease-in-out",
  "&:hover": {
    backgroundColor: "#20dbd8", // color5
    boxShadow: "0 4px 15px rgba(32, 219, 216, 0.3)", // color5'in şeffaf versiyonu
  },
}));

const HomePage = () => {
  const [buttonClicked, setButtonClicked] = useState(false);

  const handleButtonClick = () => {
    setButtonClicked(true);
  };

  return (
    <GradientBackground>
      <Typography
        variant="h3"
        component="h1"
        sx={{
          fontFamily: "Poppins, sans-serif",
          fontWeight: "700",
          letterSpacing: "0.05em",
          marginBottom: 3,
          color: "#ffffff", // Beyaz metin
        }}
      >
        Hayalindeki Rotayı Keşfet!
      </Typography>

      <SearchBar
        placeholder="Nereye gitmek istersiniz?"
        variant="outlined"
        sx={{
          marginBottom: 2,
        }}
      />

      {/* Framer Motion ile "Flight" animasyonuyla Paper */}
      <motion.div
        initial={{ x: "-100vw", opacity: 0 }} // Başlangıçta görünmez ve soldan gelir
        animate={{ x: 0, opacity: 1 }} // Orta noktaya gelir ve görünür olur
        transition={{ type: "spring", stiffness: 50, damping: 20, delay: 0.5 }}
      >
        <Paper elevation={6} sx={{ padding: "10px 20px", borderRadius: "8px" }}>
          <SearchButton onClick={handleButtonClick}>Arama Yap</SearchButton>
        </Paper>
      </motion.div>

      {/* Buton tıklandığında ekranın sağından kaybolacak şekilde animasyon */}
      {buttonClicked && (
        <motion.div
          initial={{ x: 0, opacity: 1 }} // Başlangıçta mevcut yerde ve görünür
          animate={{ x: "100vw", opacity: 0 }} // Sağ tarafa kaybolur
          transition={{ duration: 0.8, ease: "easeInOut" }}
        >
          <SearchButton>
            <i className="material-icons">flight_takeoff</i>{" "}
            {/* Kağıt Uçak İkonu */}
          </SearchButton>
        </motion.div>
      )}
    </GradientBackground>
  );
};

export default HomePage;
